import { Component, OnInit } from '@angular/core';
import { Scren } from '../screen';
import { ActivatedRoute, Router } from '@angular/router';
import { ScreService } from '../scre.service';

@Component({
  selector: 'app-update-screens',
  templateUrl: './update-screens.component.html',
  styleUrls: ['./update-screens.component.css'],
})

/**
 * @author Thejesh
 * @Description admin can update the screen details in a theatre.
 */
export class UpdateScreensComponent implements OnInit {
  screenId!: number;
  scre!: Scren;

  constructor(
    private actRouter: ActivatedRoute,
    private router: Router,
    private sService: ScreService
  ) {}

  ngOnInit(): void {
    this.scre = new Scren();
    this.screenId = this.actRouter.snapshot.params['screenId'];
    this.sService.getScreenById(this.screenId).subscribe((data) => {
      this.scre = data;
    });
  }
  updateAScreen() {
    this.sService.updateScreen(this.scre).subscribe((res) => {
      this.router.navigate(['/screen']);
    });
  }
}
<section id="detailMovie">
  <div class="container">
    <div class="details">
      <div class="row flexbox-center">
        <div class="col-lg-5 text-lg-left text-center"></div>
        <div class="col-lg-7">
          <div class="movieDetails-content">
            <h2>{{ scre.screenName }}</h2>
            <ul>
              <li>
                <div class="movieDetail-left">Screen:</div>
                <input
                  class="movieDetail-right"
                  [(ngModel)]="scre.screenName"
                  name="name"
                />
              </li>
              <li>
                <div class="movieDetail-left">Rows:</div>
                <input
                  class="movieDetail-right"
                  [(ngModel)]="scre.rows"
                  name="rows"
                />
              </li>
              <li>
                <div class="movieDetail-left">Columns:</div>
                <input
                  class="movieDetail-right"
                  [(ngModel)]="scre.columns"
                  name="columns"
                />
              </li>
            </ul>
          </div>
        </div>
      </div>
      <a [routerLink]="['/screen/view', scre.screenId]" class="theme-btn1"
        ><i class="icofont-ticket"></i> Update</a
      >
      <!--<a href="#" class="theme-btn"><i class="icofont-ticket"></i> Cancel</a>-->
    </div>
  </div>
</section>
