import { Component, OnInit } from '@angular/core';
import { Movie } from '../movie';
import { MveeService } from '../mvee.service';

@Component({
  selector: 'app-all-movies',
  templateUrl: './all-movies.component.html',
  styleUrls: ['./all-movies.component.css'],
})
export class AllMoviesComponent implements OnInit {
  movies!: any[];
  genre!: string;
  searchArray!: any[];
  sortArray!: any[];
  movieFilterValue!: string;
  constructor(private mService: MveeService) {}

  ngOnInit(): void {
    this.mService.getAllMovies().subscribe((data: Movie[]) => {
      this.movies = data;
      this.searchArray = data;
      this.sortArray = data;
    });
  }

  removeMovie(movieId: number) {
    this.mService.deleteMovie(movieId).subscribe((data: Movie) => {
      this.ngOnInit();
      console.log(data);
    });
  }

  searchByGenre() {
    this.movies = this.searchArray.filter(
      (data) => data.movieGenre == this.genre
    );
  }

  topRated() {
    this.movies = this.sortArray
      .sort((a, b) => (a.movieRating > b.movieRating ? -1 : 1))
      .slice(0, 4);
  }

  latestMovies() {
    this.movies = this.sortArray.sort((a, b) =>
      a.movieDate > b.movieDate ? -1 : 1
    );
  }
  allMovies() {
    this.ngOnInit();
  }
}
<section id="movies">
  <div class="container">
    <div class="search form-group">
      <input
        type="text"
        [(ngModel)]="movieFilterValue"
        placeholder="Search movies"
        class="form-control"
        autofocus
      />
    </div>
    <div class="row flexbox-center">
      <div class="col-lg-6 text-center text-lg-left">
        <div class="section-title">
          <h1><i class="icofont icofont-movie"></i> Movies</h1>
        </div>
      </div>
      <div class="col-lg-6 text-center text-lg-right">
        <div class="portfolio-menu">
          <ul>
            <li (click)="allMovies()" class="active">All Movies</li>
            <li (click)="topRated()">Top Rated</li>
            <li (click)="latestMovies()">Recently Released</li>
          </ul>
        </div>
      </div>
    </div>
    <hr />
  </div>
  <div class="row">
    <div class="col-9 align-self-end">
      <div class="movies-list">
        <div
          class="movies-box"
          *ngFor="
            let movie of movies | searchMovie: movieFilterValue;
            let id = index
          "
        >
          <div class="movies-img">
            <img src="assets/img/portfolio{{ id }}.png" />
          </div>
          <a [routerLink]="['view', movie.movieId]">
            {{ movie.movieName }} [{{ movie.movieLanguage }}]
          </a>
        </div>
      </div>
    </div>
  </div>
</section>
