import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Seat } from 'src/app/seat/seat';
import { SeetService } from 'src/app/seat/seet.service';
import { TktService } from '../tkt.service';

@Component({
  selector: 'app-create-ticket',
  templateUrl: './create-ticket.component.html',
  styleUrls: ['./create-ticket.component.css'],
})
export class CreateTicketComponent implements OnInit {
  ticketForm!: FormGroup;
  errorMessage!: string;
  bookingId!: number;
  seats: any[];
  constructor(
    private fb: FormBuilder,
    private tikService: TktService,
    private stService: SeetService,
    private router: Router,
    private actRouter: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.bookingId = this.actRouter.snapshot.params['bookingId'];
    this.stService.getAllSeats().subscribe((data: Seat[]) => {
      this.seats = data;
    });
    this.ticketForm = this.fb.group({
      noOfSeats: ['', Validators.required],
      seats: ['', Validators.required],
    });
  }

  addATicket() {
    console.log(this.ticketForm.value);
    this.tikService.addTicket(this.ticketForm.value, this.bookingId).subscribe(
      (res: any) => {
        this.router.navigate(['/booking/view', this.bookingId]);
      },
      (error: any) => {
        this.errorMessage = error;
      }
    );
  }
}
<section id="ticket">
  <div class="custom-container">
    <h3 style="color: red">{{ errorMessage }}</h3>
    <form [formGroup]="ticketForm" (ngSubmit)="addATicket()">
      <div class="form-row">
        <div class="form-group">
          <div class="input-data">
            <input
              type="number"
              class="form-control"
              formControlName="noOfSeats"
              required
            />
            <div class="underline"></div>
            <label for="">No of Seats</label>
          </div>
        </div>
      </div>
      <select class="form-select" multiple formControlName="seats">
        <option value="">Choose Seats</option>
        <option *ngFor="let s of seats" [ngValue]="s">
          {{ s.seatNumber }} | {{ s.type }}
        </option>
      </select>

      <div>
        <button type="submit" class="btn btn-primary">Add Booking</button>
      </div>
    </form>
  </div>
</section>
