import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { BokingService } from '../boking.service';

@Component({
  selector: 'app-create-booking',
  templateUrl: './create-booking.component.html',
  styleUrls: ['./create-booking.component.css'],
})

/**
 * @author Jyothi
 * @Description It is used to perform movie booking by the Customer.
 */
export class CreateBookingComponent implements OnInit {
  bookingForm!: FormGroup;
  errorMessage!: string;
  showId: number;
  bookingId: number;
  constructor(
    private fb: FormBuilder,
    private bService: BokingService,
    private router: Router,
    private actRouter: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.showId = this.actRouter.snapshot.params['showId'];
    this.bookingForm = this.fb.group({
      transactionMode: [''],
      transactionStatus: [''],
      bookingDate: [''],
    });
  }

  addABooking() {
    this.bService.addBooking(this.bookingForm.value, this.showId).subscribe(
      (res: any) => {
        console.log(res.transactionId);
        this.router.navigate(['ticket/add', res.transactionId]);
      },
      (error: any) => {
        this.errorMessage = error;
      }
    );
  }
}
<section id="booking">
  <div class="custom-container">
    <h3 style="color: red">{{ errorMessage }}</h3>
    <form [formGroup]="bookingForm" (ngSubmit)="addABooking()">
      <div class="form-row">
        <!-- <div class="form-group">
          <div class="input-data"> -->
        <!-- <input
              type="text"
              class="form-control"
              formControlName="transactionMode"
              required
            />
            <div class="underline"></div>
            <label for="">Transaction Mode</label> -->
        <div class="wrap">
          <input
            type="radio"
            id="rad1"
            value="admin"
            formControlName="transactionMode"
          />
          <label for="rad1">Online</label>
          <input
            type="radio"
            id="rad2"
            formControlName="transactionMode"
            value="offline"
          />
          <label for="rad2">Offline</label>
          <!-- </div>
          </div> -->
        </div>
      </div>
      <div class="form-group">
        <div class="form-row">
          <div class="date">
            <input
              type="date"
              class="form-control"
              formControlName="bookingDate"
            />
          </div>
        </div>
      </div>
      <div>
        <button [routerLink]="['/movie']" type="submit" class="btn btn-danger">
          Cancel
        </button>
        <button type="submit" class="btn btn-primary">
          Proceed <i class="icofont-long-arrow-right"></i>
        </button>
      </div>
    </form>
  </div>
</section>
